CREATE OR REPLACE FUNCTION pls_obter_dados_vendedor_vinc ( nr_seq_vendedor_vinc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p: 
	"N" - Nome vendedor vinculado
	"C" - Código de pessoa física
	"M" - E-mail
*/
ds_retorno_w			varchar(255);
nm_vendedor_w			varchar(255);
cd_pessoa_fisica_w		varchar(10);
ds_email_w			varchar(255);


BEGIN

select	substr(obter_nome_pf(cd_pessoa_fisica),1,255) nm_vendedor,
	cd_pessoa_fisica,
	obter_dados_pf_pj(cd_pessoa_fisica,null,'M') ds_email
into STRICT	nm_vendedor_w,
	cd_pessoa_fisica_w,
	ds_email_w
from	pls_vendedor_vinculado
where	nr_sequencia	= nr_seq_vendedor_vinc_p;

if (ie_opcao_p = 'N') then
	ds_retorno_w	:= nm_vendedor_w;
elsif (ie_opcao_p	= 'C') then
	ds_retorno_w	:= cd_pessoa_fisica_w;
elsif (ie_opcao_p	= 'M') then
	ds_retorno_w	:= ds_email_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
