SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_vendedor (nr_seq_vendedor_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	"N"	- Nome vendedor
	"C"	- Código da pessoa física
	"SN"	- Número de sequência do vendedor + Nome do vendedor
	"M" - Email vendedor
*/
ds_retorno_w			varchar(255);
nm_vendedor_w			varchar(255);
cd_pessoa_fisica_w		varchar(10);
ds_email_w			varchar(255);
cd_cgc_w			pessoa_juridica.cd_cgc%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;


BEGIN

select	substr(obter_nome_pf_pj(cd_pessoa_fisica,cd_cgc),1,255) nm_vendedor,
	substr(obter_dados_pf_pj(cd_pessoa_fisica,cd_cgc,'M'),1,255) ds_email,
	cd_pessoa_fisica,
	cd_cgc,
	cd_estabelecimento
into STRICT	nm_vendedor_w,
	ds_email_w,
	cd_pessoa_fisica_w,
	cd_cgc_w,
	cd_estabelecimento_w
from	pls_vendedor
where	nr_sequencia	= nr_seq_vendedor_p;

if (ie_opcao_p = 'N') then
	ds_retorno_w := nm_vendedor_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w := cd_pessoa_fisica_w;
elsif (ie_opcao_p = 'SN') then
	ds_retorno_w := nr_seq_vendedor_p || ' - ' || nm_vendedor_w;
elsif (ie_opcao_p = 'M') then

	if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		select	max(ds_email)
		into STRICT	ds_retorno_w
		from	pessoa_juridica_estab
		where	cd_cgc			= cd_cgc_w
		and	cd_estabelecimento	= cd_estabelecimento_w;
	else
		ds_retorno_w := ds_email_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
