SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_vinc_sca ( nr_seq_vinculo_sca_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao
	N = Nome do SCA
	O = Origem do SCA
	S = Sequencia da Origem do SCA
	I = Data inclusão SCA
	E = Data exclusão SCA
	C = Código SCA
	CA = Carência do SCA
*/
ds_retorno_w		varchar(300);
nm_sca_w		varchar(300);
nm_origem_w		varchar(300);
nr_seq_contrato_w	bigint;
nr_seq_plano_w		bigint;
nr_seq_segurado_w	bigint;
nr_seq_origem_w		bigint;
qt_dias_carencia_w	bigint;
dt_inicio_vigencia_w	timestamp;


BEGIN

if (ie_opcao_p	= 'N') then
	select	substr(pls_obter_dados_produto(nr_seq_plano,'N'),1,255)
	into STRICT	ds_retorno_w
	from	pls_sca_vinculo
	where	nr_sequencia = nr_seq_vinculo_sca_p;
elsif (ie_opcao_p	= 'O') then
	select	CASE WHEN nr_seq_contrato='' THEN ''  ELSE nr_seq_contrato END ,
		CASE WHEN nr_seq_plano='' THEN ''  ELSE nr_seq_plano END ,
		CASE WHEN nr_seq_segurado='' THEN ''  ELSE nr_seq_segurado END
	into STRICT	nr_seq_contrato_w,
		nr_seq_plano_w,
		nr_seq_segurado_w
	from	pls_sca_vinculo
	where	nr_sequencia = nr_seq_vinculo_sca_p;

	if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
		ds_retorno_w	:= 'Contrato';
	elsif (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
		ds_retorno_w	:= 'Segurado';
	elsif (nr_seq_plano_w IS NOT NULL AND nr_seq_plano_w::text <> '') then
		ds_retorno_w	:= 'Serviço/Cobertura adicional';
	end if;

elsif (ie_opcao_p	= 'S') then

	select	CASE WHEN nr_seq_contrato='' THEN ''  ELSE nr_seq_contrato END ,
		CASE WHEN nr_seq_plano='' THEN ''  ELSE nr_seq_plano END ,
		CASE WHEN nr_seq_segurado='' THEN ''  ELSE nr_seq_segurado END
	into STRICT	nr_seq_contrato_w,
		nr_seq_plano_w,
		nr_seq_segurado_w
	from	pls_sca_vinculo
	where	nr_sequencia = nr_seq_vinculo_sca_p;

	if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
		ds_retorno_w	:= nr_seq_contrato_w;
	elsif (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
		ds_retorno_w	:= nr_seq_segurado_w;
	elsif (nr_seq_plano_w IS NOT NULL AND nr_seq_plano_w::text <> '') then
		ds_retorno_w	:= nr_seq_plano_w;
	end if;

elsif (ie_opcao_p	= 'I') then
	select	to_char(dt_inicio_vigencia,'dd/mm/yyyy')
	into STRICT	ds_retorno_w
	from	pls_sca_vinculo
	where	nr_sequencia = nr_seq_vinculo_sca_p;

elsif (ie_opcao_p	= 'E') then
	select	to_char(dt_fim_vigencia,'dd/mm/yyyy')
	into STRICT	ds_retorno_w
	from	pls_sca_vinculo
	where	nr_sequencia = nr_seq_vinculo_sca_p;
elsif (ie_opcao_p	= 'C') then
	select	b.cd_plano
	into STRICT	ds_retorno_w
	from	pls_sca_vinculo		a,
		pls_plano		b
	where	a.nr_seq_plano		= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_vinculo_sca_p;

elsif (ie_opcao_p	= 'CA') then
	select	max(qt_dias)
	into STRICT	qt_dias_carencia_w
	from	pls_carencia		c,
		pls_sca_vinculo		b,
		pls_plano		a
	where	b.nr_seq_plano		= a.nr_sequencia
	and	c.nr_seq_plano		= a.nr_sequencia
	and	b.nr_sequencia		= nr_seq_vinculo_sca_p
	and	c.ie_cpt		= 'N';

	if (qt_dias_carencia_w IS NOT NULL AND qt_dias_carencia_w::text <> '') then

		select	dt_inicio_vigencia
		into STRICT	dt_inicio_vigencia_w
		from	pls_sca_vinculo
		where	nr_sequencia = nr_seq_vinculo_sca_p;

		ds_retorno_w	:= to_char(dt_inicio_vigencia_w + qt_dias_carencia_w,'dd/mm/yyyy');
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_vinc_sca ( nr_seq_vinculo_sca_p bigint, ie_opcao_p text) FROM PUBLIC;

