SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_xml_arquiv_web (nr_seq_protocolo_p bigint, ie_tipo_opcao_p text, ie_nova_imp_p text) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter os dados do arquivo XML
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
	N - Nome
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w		varchar(255);
nr_seq_xml_arquivo_w	bigint;
ie_nova_imp_w		varchar(1);


BEGIN

ie_nova_imp_w := coalesce(ie_nova_imp_p, 'N');

if (ie_nova_imp_w = 'S') then

	select  max(nr_seq_xml_arquivo)
	into STRICT	nr_seq_xml_arquivo_w
	from 	pls_protocolo_conta_imp
	where   nr_sequencia	= nr_seq_protocolo_p;

else

	select  max(nr_seq_xml_arquivo)
	into STRICT	nr_seq_xml_arquivo_w
	from 	pls_protocolo_conta
	where   nr_sequencia = nr_seq_protocolo_p;
end if;


if (nr_seq_xml_arquivo_w IS NOT NULL AND nr_seq_xml_arquivo_w::text <> '') then

	if (ie_tipo_opcao_p = 'N') then
		select	substr(max(nm_arquivo),1,255)
		into STRICT	ds_retorno_w
		from	pls_xml_arquivo
		where	nr_sequencia = nr_seq_xml_arquivo_w;
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_xml_arquiv_web (nr_seq_protocolo_p bigint, ie_tipo_opcao_p text, ie_nova_imp_p text) FROM PUBLIC;

