CREATE OR REPLACE FUNCTION pls_obter_dado_acao_fat_geral ( nr_seq_regra_p pls_regra_tit_fat_geral.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*	ie_opcao_p
		VCN	- Vincular o A580 em um titulo de câmara de compensação
*/
ds_retorno_w	varchar(500) :='';

BEGIN

if (ie_opcao_p = 'VCN') then

	select	coalesce(max(a.ie_consiste_tit_camara), 'N')
	into STRICT	ds_retorno_w
	from	pls_regra_tit_fat_geral	a
	where	a.nr_sequencia			= nr_seq_regra_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
