CREATE OR REPLACE FUNCTION pls_obter_dado_regra_serv ( nr_seq_regra_servico_p pls_regra_preco_servico.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*	ie_opcao_p:	 
 
		DS - Descricao 
*/
 
ds_retorno_w	varchar(500);

BEGIN
ds_retorno_w := '';
if (nr_seq_regra_servico_p IS NOT NULL AND nr_seq_regra_servico_p::text <> '') then 
 
	if (ie_opcao_p = 'DS') then 
		begin 
			select	ds_regra 
			into STRICT	ds_retorno_w 
			from	pls_regra_preco_servico 
			where	nr_sequencia	= nr_seq_regra_servico_p;
		exception 
			when no_data_found then ds_retorno_w := null;			
		end;
	end if;
 
end if;
return 	ds_retorno_w;
end;
