CREATE OR REPLACE FUNCTION pls_obter_dado_tipo_ac_pacote ( nr_seq_tipo_acomodacao_p pls_pacote_tipo_acomodacao.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*	ie_opcao_p:
		NM - Nome
*/
ds_retorno_w	varchar(4000);


BEGIN

ds_retorno_w := null;

if (ie_opcao_p = 'NM') then

	select	max(a.ds_regra)
	into STRICT	ds_retorno_w
	from	pls_pacote_tipo_acomodacao	a
	where	a.nr_sequencia			= nr_seq_tipo_acomodacao_p;

end if;


return ds_retorno_w;

end;
$body$
