SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_datas_mensalidade ( nr_seq_mensalidade_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

/* ie_opcao_p
	D - Data de referencia
	LT - Liquidacao do titulo
	PP - Pagamento Previsto
*/
dt_liquidacao_w			timestamp;
dt_pagamento_previsto_w		timestamp;
dt_referencia_w			timestamp;


BEGIN

if (ie_opcao_p = 'LT') then
	select	max(a.dt_liquidacao)
	into STRICT	dt_liquidacao_w
	from	titulo_receber	a,
		pls_mensalidade	b
	where	a.nr_seq_mensalidade	= b.nr_sequencia
	and	b.nr_sequencia		= nr_seq_mensalidade_p;
	return dt_liquidacao_w;
elsif (ie_opcao_p = 'PP') then
	select	max(a.dt_pagamento_previsto)
	into STRICT	dt_pagamento_previsto_w
	from	titulo_receber	a,
		pls_mensalidade	b
	where	a.nr_seq_mensalidade	= b.nr_sequencia
	and	b.nr_sequencia		= nr_seq_mensalidade_p;
	return dt_pagamento_previsto_w;
elsif (ie_opcao_p = 'D') then
  select max(dt_referencia)
  into STRICT	dt_referencia_w
  from	pls_mensalidade
  where	nr_sequencia	= nr_seq_mensalidade_p;
	return dt_referencia_w;
end if;

return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_datas_mensalidade ( nr_seq_mensalidade_p bigint, ie_opcao_p text) FROM PUBLIC;

