SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_datas_periodo_tps ( nr_ano_p text, nr_periodo_tps_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
dt_inicial_w		varchar(10);
dt_final_w		varchar(10);
dt_inicio_comp_w	timestamp;
dt_fim_comp_w		timestamp;
nr_ano_w		smallint;


BEGIN

select	dt_mes_referencia_inicial,
	dt_mes_referencia_final
into STRICT	dt_inicial_w,
	dt_final_w
from	pls_periodo_tps
where	nr_sequencia	= nr_periodo_tps_p;

if (dt_inicial_w IS NOT NULL AND dt_inicial_w::text <> '') and (dt_final_w IS NOT NULL AND dt_final_w::text <> '') and (dt_inicial_w <> '  /  ') and /* Tratado pois se estava vazio dava problema ao jogar pelo delphi no campo de data */
	(dt_final_w <> '  /  ') then
	begin /* Tratado para se informar dia do mês inválido */
	dt_inicio_comp_w		:= to_date(dt_inicial_w||'/'||nr_ano_p);
	exception
	when others then
		dt_inicial_w		:= to_char((substr(dt_inicial_w,1,2))::numeric  -1) || substr(dt_inicial_w,3,3);
		dt_inicio_comp_w		:= to_date(dt_inicial_w||'/'||nr_ano_p);
	end;

	begin /* Tratado para se informar dia do mês inválido */
	dt_fim_comp_w		:= to_date(dt_final_w||'/'||nr_ano_p);
	exception
	when others then
		dt_final_w		:= to_char((substr(dt_final_w,1,2))::numeric  -1) || substr(dt_final_w,3,3);
		dt_fim_comp_w		:= to_date(dt_final_w||'/'||nr_ano_p);
	end;

	if (dt_inicio_comp_w > dt_fim_comp_w) then /* Tratamento para ver se o período inicial precisa ser do ano anterior -  OS 421251 */
		nr_ano_w	:= (nr_ano_p)::numeric  - 1;
	else
		nr_ano_w	:= nr_ano_p;
	end if;

	ds_retorno_w	:= dt_inicial_w||'/'||nr_ano_w||';'||dt_final_w||'/'||nr_ano_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
