SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_datas_proposta_on ( nr_seq_proposta_p pls_proposta_online.nr_sequencia%type, ie_data_p text) RETURNS timestamp AS $body$
DECLARE


dt_proposta_w	timestamp	:= null;
ie_estagio_w	pls_proposta_online.ie_estagio%type;
cd_estabelecimento_w	pls_proposta_online.cd_estabelecimento%type;


BEGIN

if (ie_data_p = '1') then
	select	dt_criacao
	into STRICT	dt_proposta_w
	from	pls_proposta_online	
	where	nr_sequencia	= nr_seq_proposta_p;


elsif (ie_data_p = '2') then
	select	dt_confirmacao
	into STRICT	dt_proposta_w
	from	pls_proposta_online		
	where	nr_sequencia = nr_seq_proposta_p;

elsif (ie_data_p = '3') then
	select	dt_vigencia_prev
	into STRICT	dt_proposta_w
	from	pls_proposta_online		
	where	nr_sequencia = nr_seq_proposta_p;

elsif (ie_data_p = '4') then
	select	dt_validacao
	into STRICT	dt_proposta_w
	from	pls_proposta_online		
	where	nr_sequencia = nr_seq_proposta_p;

elsif (ie_data_p = '5') then
	
	select	a.dt_criacao + (select coalesce(max(x.qt_dias_limite_contratacao),0) from pls_param_proposta_online x where x.cd_estabelecimento = a.cd_estabelecimento)
	into STRICT	dt_proposta_w
	from	pls_proposta_online a	
	where	a.nr_sequencia = nr_seq_proposta_p;
	
elsif (ie_data_p = '6') then
	select	ie_estagio,
		cd_estabelecimento
	into STRICT	ie_estagio_w,
		cd_estabelecimento_w
	from	pls_proposta_online		
	where	nr_sequencia = nr_seq_proposta_p;
	
	if (ie_estagio_w in ('AC','CP')) then
		select	max(dt_criacao)
		into STRICT	dt_proposta_w
		from	pls_proposta_hist_online
		where	nr_seq_prop_online =  nr_seq_proposta_p
		and	ie_tipo_historico in ('SC','SA');
		
		if (dt_proposta_w IS NOT NULL AND dt_proposta_w::text <> '') then
			select	dt_proposta_w + coalesce(max(qt_dias_limite_complemento),0)
			into STRICT	dt_proposta_w
			from	pls_param_proposta_online
			where	cd_estabelecimento = cd_estabelecimento_w;
		end if;
	end if;
	
end if;

return	dt_proposta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_datas_proposta_on ( nr_seq_proposta_p pls_proposta_online.nr_sequencia%type, ie_data_p text) FROM PUBLIC;

