CREATE OR REPLACE FUNCTION pls_obter_data_alt_pessoa ( cd_pessoa_fisica_p text, dt_mes_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_log_w		bigint;
dt_alteracao_w		timestamp;



BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_log_w
from	tasy_log_alteracao
where	nm_tabela	in ('PESSOA_FISICA','COMPL_PESSOA_FISICA')
and (ds_chave_simples = cd_pessoa_fisica_p or
	ds_chave_composta like '%CD_PESSOA_FISICA=' || cd_pessoa_fisica_p || '%')
and	trunc(dt_atualizacao,'month') = trunc(dt_mes_referencia_p,'month');

if (nr_seq_log_w > 0) then
	select	max(a.dt_atualizacao)
	into STRICT	dt_alteracao_w
	from	tasy_log_alt_campo a,
		tasy_log_alteracao b
	where	a.nr_seq_log_alteracao	= b.nr_sequencia
	and	trunc(b.dt_atualizacao,'month') = trunc(dt_mes_referencia_p,'month')
	and	b.nm_tabela	in ('PESSOA_FISICA','COMPL_PESSOA_FISICA')
	and (b.ds_chave_simples = cd_pessoa_fisica_p or
		b.ds_chave_composta like '%CD_PESSOA_FISICA=' || cd_pessoa_fisica_p || '%')
	and	upper(nm_atributo) in (	'NM_PESSOA_FISICA','DT_NASCIMENTO','IE_SEXO','NM_MAE_PESSOA_FISICA','DS_ENDERECO','NR_ENDERECO','DS_BAIRRO',
					'CD_MUNICIPIO_IBGE','SG_ESTADO','CD_CEP','NR_CARTAO_NAC_SUS','NR_IDENTIDADE','DS_ORGAO_EMISSOR_CI','CD_NACIONALIDADE',
					'NR_PIS_PASEP','NR_CPF','CD_PESSOA_FISICA_REF');

	if (dt_alteracao_w IS NOT NULL AND dt_alteracao_w::text <> '') then
		ds_retorno_w	:= to_char(dt_alteracao_w,'dd/mm/yyyy');
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
