SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_data_arquivo_dev_sib ( nr_seq_lote_p bigint, ds_arquivo_p text) RETURNS varchar AS $body$
DECLARE


dt_retorno_w		varchar(10);
ds_arquivo_w		varchar(100);
dt_retorno_ww		timestamp;
dt_mesano_referencia_w	timestamp;
ie_data_lote_w		varchar(10) := 'S';


BEGIN

begin
ds_arquivo_w	:= substr(ds_arquivo_p,7,8);
dt_retorno_w	:= substr(ds_arquivo_w,7,2) || substr(ds_arquivo_w,5,2) || substr(ds_arquivo_w,1,4);
dt_retorno_ww	:= to_date(dt_retorno_w,'dd/mm/yyyy');
exception
when others then
	dt_retorno_ww	:= null;
end;

select	max(dt_mesano_referencia)
into STRICT	dt_mesano_referencia_w
from	pls_lote_sib
where	nr_sequencia	= nr_seq_lote_p;

if (dt_retorno_ww IS NOT NULL AND dt_retorno_ww::text <> '') then
	if	trunc(dt_mesano_referencia_w,'Month') <> trunc(dt_retorno_ww,'Month') then
		ie_data_lote_w	:= 'N';
	end if;
end if;

return	ie_data_lote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
