CREATE OR REPLACE FUNCTION pls_obter_data_cobranca_sca ( nr_seq_plano_p bigint, nr_seq_segurado_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_mes_previsao_sca_w	timestamp;
qt_meses_cobranca_w	bigint;
dt_adesao_segurado_w	timestamp;
			

BEGIN

begin
select	max(dt_inicio_vigencia)
into STRICT	dt_adesao_segurado_w
from	pls_sca_vinculo
where	nr_seq_plano	= nr_seq_plano_p
and	nr_seq_segurado	= nr_seq_segurado_p;
exception
when others then
	dt_adesao_segurado_w	:= null;
end;

select	coalesce(qt_parcelas_cobranca,0)
into STRICT	qt_meses_cobranca_w
from	pls_plano
where	nr_sequencia	= nr_seq_plano_p;

if (coalesce(dt_adesao_segurado_w::text, '') = '') then
	select	trunc(dt_contratacao,'Month')
	into STRICT	dt_adesao_segurado_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_p;
end if;	

if (qt_meses_cobranca_w <> 0) then
	begin
		dt_mes_previsao_sca_w	:= add_months(dt_adesao_segurado_w,qt_meses_cobranca_w);
	exception
	when others then
		dt_mes_previsao_sca_w	:= null;	
	end;
else
	dt_mes_previsao_sca_w	:= null;
end if;	

return	dt_mes_previsao_sca_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
