CREATE OR REPLACE FUNCTION pls_obter_data_contab_ppcng ( ie_cancelamento_p text, dt_cancelamento_p timestamp, dt_inicio_cobertura_p timestamp, dt_mesano_referencia_p timestamp, nr_seq_item_cancel_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_contabil_w		timestamp;


BEGIN

if (ie_cancelamento_p = 'E') then
	if (dt_cancelamento_p >= dt_mesano_referencia_p) then
		dt_contabil_w	:= dt_cancelamento_p;
	else
		dt_contabil_w	:= dt_inicio_cobertura_p;
	end if;

else
	if (dt_inicio_cobertura_p IS NOT NULL AND dt_inicio_cobertura_p::text <> '') then
		dt_contabil_w	:= dt_inicio_cobertura_p;
	else
		select 	max(y.dt_inicio_cobertura)
		into STRICT	dt_contabil_w
		from	pls_mensalidade_segurado y,
			pls_mensalidade_seg_item x
		where	y.nr_sequencia	= x.nr_seq_mensalidade_seg
		and	x.nr_sequencia	= nr_seq_item_cancel_p;
	end if;

end if;

return	dt_contabil_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
