CREATE OR REPLACE FUNCTION pls_obter_data_dias_uteis ( dt_inicial_p timestamp, qt_dias_p bigint, cd_estabelecimento_p bigint) RETURNS timestamp AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar a data futura considerando só dias uteís.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [X]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_auxiliar_w			bigint	:= 1;
dt_auxiliar_w			timestamp;
dt_auxiliar2_w			timestamp;
dt_retorno_w			timestamp;


BEGIN

dt_auxiliar_w	:= trunc(dt_inicial_p);

while qt_auxiliar_w <= qt_dias_p loop
	begin
	dt_auxiliar2_w	:= dt_auxiliar_w + 1;

	if (obter_se_dia_util(dt_auxiliar2_w, cd_estabelecimento_p)	= 'S') then
		qt_auxiliar_w	:= qt_auxiliar_w + 1;
	end if;

	dt_auxiliar_w	:= dt_auxiliar2_w;
	end;
end loop;

dt_retorno_w	:= dt_auxiliar2_w;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
