SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_data_ger_analise ( nr_seq_lote_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_lote_w			timestamp;
qt_lote_falha_w			integer;

BEGIN

if (coalesce(nr_seq_lote_p,0) > 0) then
	select	count(1)
	into STRICT	qt_lote_falha_w
	from	pls_lote_protocolo_conta
	where	nr_sequencia 	= nr_seq_lote_p
	and	ie_status	= 'X';

	if (qt_lote_falha_w	= 0) then
		select 	max(dt_geracao_analise)
		into STRICT	dt_lote_w
		from	pls_lote_protocolo_conta
		where	nr_sequencia = nr_seq_lote_p;
	end if;
end if;

return dt_lote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
