CREATE OR REPLACE FUNCTION pls_obter_data_liquidacao_reem (nr_seq_protocolo_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_liquidacao_w		timestamp	:= null;


BEGIN
if (coalesce(nr_seq_protocolo_p,0) > 0) then

	select	max(b.dt_liquidacao)
	into STRICT	dt_liquidacao_w
	from  	pls_protocolo_conta  a,
		titulo_pagar    b
	where  	a.nr_sequencia  = b.nr_seq_reembolso
	and  	a.nr_sequencia   = nr_seq_protocolo_p;

end if;
return	dt_liquidacao_w;

end;
