SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_data_preco_item ( nr_seq_item_p bigint, ie_tipo_item_p text) RETURNS timestamp AS $body$
DECLARE


/* IE_TIPO_ITEM_P
	P - Procedimento
	M - Material
*/
dt_item_w	timestamp;


BEGIN

-- Pegar a data para o procedimento
if (ie_tipo_item_p	= 'P') then

	select	a.dt_procedimento
	into STRICT	dt_item_w
	from	pls_conta_proc_v	a
	where	a.nr_sequencia	= nr_seq_item_p;

-- Pegar a data para o material
elsif (ie_tipo_item_p	= 'M') then

	select	a.dt_atendimento
	into STRICT	dt_item_w
	from	pls_conta_mat_v		a
	where	a.nr_sequencia = nr_seq_item_p;

end if;

return	dt_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
