SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_data_prev_rescisao ( nr_seq_segurado_p bigint) RETURNS timestamp AS $body$
DECLARE

dt_prev_rescisao_w		timestamp;
nr_seq_rescisao_w		bigint;


BEGIN

begin
	select	b.dt_solicitacao
	into STRICT	dt_prev_rescisao_w
	from	pls_solic_rescisao_benef a,
		pls_solicitacao_rescisao b
	where	b.nr_sequencia		= a.nr_seq_solicitacao
	and	a.nr_sequencia		= (	SELECT 	max(x.nr_sequencia)
						from	pls_solic_rescisao_benef x,
							pls_solicitacao_rescisao y
						where	y.nr_sequencia		= x.nr_seq_solicitacao
						and	x.nr_seq_segurado	= nr_seq_segurado_p
						and	y.ie_status in (1,2));
exception
when others then
	dt_prev_rescisao_w	:= null;
end;

if (coalesce(dt_prev_rescisao_w::text, '') = '') then
	select  coalesce(max(a.nr_sequencia), 0)
	into STRICT	nr_seq_rescisao_w
	from    pls_rescisao_contrato a,
		pls_segurado b
	where   a.nr_seq_segurado = b.nr_sequencia
	and     b.nr_sequencia = nr_seq_segurado_p
	and     coalesce(b.dt_rescisao::text, '') = '';

	if (nr_seq_rescisao_w > 0) then
		select  dt_rescisao
		into STRICT	dt_prev_rescisao_w
		from    pls_rescisao_contrato
		where   nr_sequencia = nr_seq_rescisao_w
		and     ie_situacao = 'A';

	end if;
end if;

return	dt_prev_rescisao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_data_prev_rescisao ( nr_seq_segurado_p bigint) FROM PUBLIC;

