SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_data_rescisao_seg ( nr_seq_segurado_p bigint, ie_venc_cartao_rescisao_p text default 'S', ie_ultimo_dia_p text default 'N') RETURNS timestamp AS $body$
DECLARE


dt_rescisao_programada_w	timestamp;
nr_seq_contrato_w		bigint;
nr_seq_pagador_w		bigint;
nr_seq_intercambio_w		bigint;
qt_registros_w			bigint;
dt_rescisao_w			timestamp;
dt_limite_utilizacao_w		timestamp;


BEGIN

dt_rescisao_programada_w	:= null;

select	nr_seq_contrato,
	nr_seq_pagador,
	nr_seq_intercambio,
	dt_rescisao
into STRICT	nr_seq_contrato_w,
	nr_seq_pagador_w,
	nr_seq_intercambio_w,
	dt_rescisao_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

select	count(*)
into STRICT	qt_registros_w
from	pls_rescisao_contrato
where	nr_seq_segurado	= nr_seq_segurado_p
and	ie_situacao	= 'A';

if (coalesce(dt_rescisao_w::text, '') = '') then
	/*Buscar a rescisão programada do beneficiário*/

	if (qt_registros_w > 0) then
		select	max(dt_rescisao),
			max(dt_limite_utilizacao)
		into STRICT	dt_rescisao_programada_w,
			dt_limite_utilizacao_w
		from	pls_rescisao_contrato
		where	nr_seq_segurado	= nr_seq_segurado_p
		and	ie_situacao	= 'A'
		and 	((ie_venc_cartao_rescisao_p = 'S') or (ie_venc_cartao_rescisao_p = 'M' AND ie_processo <> 'W'));
	else
		/*Caso não esistir no beneficiário, buscar a rescisão programada do pagador*/

		select	count(*)
		into STRICT	qt_registros_w
		from	pls_rescisao_contrato
		where	nr_seq_pagador	= nr_seq_pagador_w
		and	ie_situacao	= 'A'
		and 	((ie_venc_cartao_rescisao_p = 'S') or (ie_venc_cartao_rescisao_p = 'M' AND ie_processo <> 'W'));

		if (qt_registros_w > 0) then
			select	max(dt_rescisao),
				max(dt_limite_utilizacao)
			into STRICT	dt_rescisao_programada_w,
				dt_limite_utilizacao_w
			from	pls_rescisao_contrato
			where	nr_seq_pagador	= nr_seq_pagador_w
			and	ie_situacao	= 'A'
			and 	((ie_venc_cartao_rescisao_p = 'S') or (ie_venc_cartao_rescisao_p = 'M' AND ie_processo <> 'W'));
		else
			/*Caso não esistir no pagador , buscar a rescisão programada do contrato ou do intercâmbio*/

			if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
				select	count(*)
				into STRICT	qt_registros_w
				from	pls_rescisao_contrato
				where	nr_seq_contrato	= nr_seq_contrato_w
				and	coalesce(nr_seq_segurado::text, '') = ''
				and	ie_situacao	= 'A'
				and 	((ie_venc_cartao_rescisao_p = 'S') or (ie_venc_cartao_rescisao_p = 'M' AND ie_processo <> 'W'));

				if (qt_registros_w > 0) then
					select	max(dt_rescisao),
						max(dt_limite_utilizacao)
					into STRICT	dt_rescisao_programada_w,
						dt_limite_utilizacao_w
					from	pls_rescisao_contrato
					where	nr_seq_contrato	= nr_seq_contrato_w
					and	ie_situacao	= 'A'
					and 	((ie_venc_cartao_rescisao_p = 'S') or (ie_venc_cartao_rescisao_p = 'M' AND ie_processo <> 'W'));
				end if;
			elsif (nr_seq_intercambio_w IS NOT NULL AND nr_seq_intercambio_w::text <> '') then
				select	count(*)
				into STRICT	qt_registros_w
				from	pls_rescisao_contrato
				where	nr_seq_intercambio	= nr_seq_intercambio_w
				and	ie_situacao	= 'A'
				and 	((ie_venc_cartao_rescisao_p = 'S') or (ie_venc_cartao_rescisao_p = 'M' AND ie_processo <> 'W'));

				if (qt_registros_w > 0) then
					select	max(dt_rescisao),
						max(dt_limite_utilizacao)
					into STRICT	dt_rescisao_programada_w,
						dt_limite_utilizacao_w
					from	pls_rescisao_contrato
					where	nr_seq_intercambio	= nr_seq_intercambio_w
					and	ie_situacao	= 'A'
					and 	((ie_venc_cartao_rescisao_p = 'S') or (ie_venc_cartao_rescisao_p = 'M' AND ie_processo <> 'W'));
				end if;
			end if;
		end if;
	end if;
end if;

if (ie_ultimo_dia_p = 'S') then
	return	dt_limite_utilizacao_w;
else
	return	dt_rescisao_programada_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_data_rescisao_seg ( nr_seq_segurado_p bigint, ie_venc_cartao_rescisao_p text default 'S', ie_ultimo_dia_p text default 'N') FROM PUBLIC;

