SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_data_venc_notif_tit (nr_seq_notific_pagador_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(255);
ds_retorno_w		varchar(255);


C01 CURSOR FOR
	SELECT	to_char(a.dt_vencimento_titulo,'mm/yyyy') dt_vencimento
	from	pls_notificacao_pagador b,
		pls_notificacao_item 	a
	where	a.nr_seq_notific_pagador = b.nr_sequencia
	and	b.nr_sequencia 		 = nr_seq_notific_pagador_p
	and	exists (SELECT	1
			from	pls_mensalidade	y,
				titulo_receber	w
			where	w.nr_titulo 		= a.nr_titulo
			and	w.nr_seq_mensalidade 	= y.nr_sequencia
			and	coalesce(w.dt_liquidacao::text, '') = '')
	group by
		a.dt_vencimento_titulo
	order by a.dt_vencimento_titulo;


BEGIN
open C01;
loop
fetch C01 into
	ds_resultado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w := ds_retorno_w || ds_resultado_w || ', ';
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
