CREATE OR REPLACE FUNCTION pls_obter_debitos_pf ( cd_pessoa_fisica_p text, ie_tipo_consistencia_p text, ie_data_vencimento_p text default 'P', qt_dias_inadimplente_p bigint default 0) RETURNS bigint AS $body$
DECLARE

/* ie_tipo_consistencia_p
	COD = Código da pessoa física
	CPF = CPF da pessoa física

    ie_data_vencimento_p
	P = Pagamento previsto (Prorrogado)
	O = Vencimento Original

    qt_dias_inadimplente_p - Quantidade de dias para considerar o título como inadimplente
*/
vl_retorno_w			titulo_receber.vl_titulo%type	:= 0;
nr_cpf_w			pessoa_fisica.nr_cpf%type;
dt_sysdate_trunc_w		timestamp;


BEGIN
dt_sysdate_trunc_w	:= trunc(clock_timestamp()-qt_dias_inadimplente_p,'dd');
if (ie_tipo_consistencia_p = 'COD') then
	begin
	select	sum(t.vl_titulo)
	into STRICT	vl_retorno_w
	from	titulo_receber t
	where	t.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	t.ie_situacao = '1'
	and	((ie_data_vencimento_p = 'P' and t.dt_pagamento_previsto < dt_sysdate_trunc_w)
	or (ie_data_vencimento_p = 'O' and t.dt_vencimento < dt_sysdate_trunc_w));
	exception
	when others then
		vl_retorno_w	:= 0;
	end;
elsif (ie_tipo_consistencia_p = 'CPF') then
	select	max(nr_cpf)
	into STRICT	nr_cpf_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

	if (coalesce(nr_cpf_w,'0') <> '0') then
		begin
		select	sum(t.vl_titulo)
		into STRICT	vl_retorno_w
		from	titulo_receber t,
			pessoa_fisica p
		where	t.cd_pessoa_fisica = p.cd_pessoa_fisica
		and	p.nr_cpf	= nr_cpf_w
		and	t.ie_situacao = '1'
		and	((ie_data_vencimento_p = 'P' and t.dt_pagamento_previsto < dt_sysdate_trunc_w)
		or (ie_data_vencimento_p = 'O' and t.dt_vencimento < dt_sysdate_trunc_w));
		exception
		when others then
			vl_retorno_w	:= 0;
		end;
	end if;
end if;

return coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
