CREATE OR REPLACE FUNCTION pls_obter_debitos_pj ( cd_cgc_p text) RETURNS varchar AS $body$
DECLARE



vl_retorno_w		double precision;
ie_retorno_w		varchar(1) := 'N';


BEGIN


select	coalesce(sum(vl_titulo),0)
into STRICT	vl_retorno_w
from	titulo_receber
where	cd_cgc = cd_cgc_p
and	ie_situacao in ('1','6')
and	trunc(dt_pagamento_previsto,'dd')	<= trunc(clock_timestamp(),'dd');

if (vl_retorno_w	= 0) then
	ie_retorno_w	:= 'N';
elsif (vl_retorno_w	> 0) then
	ie_retorno_w	:= 'S';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
