CREATE OR REPLACE FUNCTION pls_obter_declaracao_saude_ant ( nr_seq_proposta_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
dt_declaracao_ant_w	timestamp;



BEGIN

select	max(DT_DECLARACAo)
into STRICT	dt_declaracao_ant_w
from	pls_declaracao_segurado
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	NR_SEQ_PROPOSTA_ADESAO	<> nr_seq_proposta_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

if (dt_declaracao_ant_w IS NOT NULL AND dt_declaracao_ant_w::text <> '') then
	ds_retorno_w	:= 'Possui DS realizada no dia ' || to_char(dt_declaracao_ant_w,'dd/mm/yyyy');
else
	ds_retorno_w	:= 'NÃ£o possui DS';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
