SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dec_saude_proposta ( nr_seq_benef_proposta_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE


/* IE_TIPO_RETONO_P
	'C' - Código
	'D' - Descrição

	1 - Não possui declaração
	2 - Possui DS liberada
	3 - Possui DS não liberada
*/
ds_retorno_w			varchar(255);
nr_seq_proposta_w		bigint;
cd_pessoa_fisica_w		varchar(10);
nr_seq_declaracao_w		bigint;
dt_liberacao_w			timestamp;


BEGIN

ds_retorno_w	:= '1';

begin
select	nr_seq_proposta,
	cd_beneficiario
into STRICT	nr_seq_proposta_w,
	cd_pessoa_fisica_w
from	pls_proposta_beneficiario
where	nr_sequencia	= nr_seq_benef_proposta_p;
exception
	when others then
	nr_seq_proposta_w	:= 0;
	cd_pessoa_fisica_w	:= '';
end;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_declaracao_w
from	pls_declaracao_segurado
where	nr_seq_proposta_adesao	= nr_seq_proposta_w
and	cd_pessoa_fisica	= cd_pessoa_fisica_w;

if (nr_seq_declaracao_w	> 0) then
	select	coalesce(max(dt_liberacao),'')
	into STRICT	dt_liberacao_w
	from	pls_declaracao_segurado
	where	nr_sequencia	= nr_seq_declaracao_w;

	if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		ds_retorno_w	:= '2';
	else
		ds_retorno_w	:= '3';
	end if;
end if;

if (ie_tipo_retorno_p	= 'D') then
	if (ds_retorno_w	= '1') then
		ds_retorno_w	:= 'Não possui DS';
	elsif (ds_retorno_w	= '2') then
		ds_retorno_w	:= 'Possui DS liberada';
	elsif (ds_retorno_w	= '3') then
		ds_retorno_w	:= 'Possui DS não liberada';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
