CREATE OR REPLACE FUNCTION pls_obter_define_gera_analise ( cd_estabelecimento_p bigint, ie_origem_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obtém se é gerada análise...
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ x]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (ie_origem_p = 'R') then
	begin
		select	coalesce(ie_define_se_gera_analise,'A')
		into STRICT	ds_retorno_w
		from	pls_param_requisicao
		where	cd_estabelecimento = cd_estabelecimento_p;
	exception
	when others then
		ds_retorno_w := 'A';
	end;
elsif (ie_origem_p = 'A') then
	begin
		select	coalesce(ie_define_se_gera_analise,'A')
		into STRICT	ds_retorno_w
		from	pls_param_autorizacao
		where	cd_estabelecimento = cd_estabelecimento_p;
	exception
	when others then
		ds_retorno_w	:= null;
	end;

	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(ie_define_se_gera_analise)
		into STRICT	ds_retorno_w
		from	pls_param_autorizacao;

		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= 'A';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
