CREATE OR REPLACE FUNCTION pls_obter_dependentes_relat ( nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
nm_pessoa_fisica_w	pessoa_fisica.nm_pessoa_fisica%type;

C01 CURSOR FOR
	SELECT	b.nm_pessoa_fisica
	from	pessoa_fisica		b,
		pls_segurado		a
	where	b.cd_pessoa_fisica	= a.cd_pessoa_fisica
	and	a.nr_seq_contrato	= nr_seq_contrato_p
	and	(a.nr_seq_titular IS NOT NULL AND a.nr_seq_titular::text <> '')
	and	((coalesce(a.dt_rescisao::text, '') = '') or (a.dt_rescisao > clock_timestamp()))
	order by b.nm_pessoa_fisica;




BEGIN

open C01;
loop
fetch C01 into
	nm_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w	:= ds_retorno_w||chr(10)||nm_pessoa_fisica_w;

	end;
end loop;
close C01;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
