SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_depend_sem_cpf ( nr_seq_proposta_p bigint, nr_seq_prop_benef_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255)	:= '';
nm_segurado_w			varchar(255);
qt_registros_w			bigint;
vl_auxiliar_w			bigint	:= 0;
ie_separador_w			varchar(3);

C01 CURSOR FOR 
	SELECT	substr(obter_nome_pf(a.cd_beneficiario),1,255) 
	from	pls_proposta_beneficiario a, 
		pessoa_fisica b 
	where (a.nr_seq_proposta	= nr_seq_proposta_p or coalesce(nr_seq_proposta_p,0) = 0) 
	and (a.nr_sequencia		= nr_seq_prop_benef_p or coalesce(nr_seq_prop_benef_p,0) = 0) 
	and	a.cd_beneficiario	= b.cd_pessoa_fisica 
	and	coalesce(b.nr_cpf::text, '') = '';


BEGIN 
 
select	count(*) 
into STRICT	qt_registros_w 
from	pls_proposta_beneficiario a, 
	pessoa_fisica b 
where (a.nr_seq_proposta	= nr_seq_proposta_p or coalesce(nr_seq_proposta_p,0) = 0) 
and (a.nr_sequencia		= nr_seq_prop_benef_p or coalesce(nr_seq_prop_benef_p,0) = 0) 
and	a.cd_beneficiario	= b.cd_pessoa_fisica 
and	coalesce(b.nr_cpf::text, '') = '';
 
open C01;
loop 
fetch C01 into 
	nm_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	vl_auxiliar_w	:= vl_auxiliar_w + 1;
	 
	select	CASE WHEN vl_auxiliar_w=qt_registros_w-1 THEN ' e '  ELSE ', ' END  
	into STRICT	ie_separador_w 
	;
	 
	ds_retorno_w	:= substr(ds_retorno_w || nm_segurado_w || ie_separador_w,1,255);
	end;
end loop;
close C01;
 
return	substr(ds_retorno_w,1,length(ds_retorno_w)-2);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
