CREATE OR REPLACE FUNCTION pls_obter_depen_nao_suspenso ( nr_seq_titular_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
qt_retorno_w		bigint;

/*
ie_retorno_w
S - Beneficiário titular possui depedente
I - Beneficiário titular não possui dependetes
*/
BEGIN

select	count(*)
into STRICT	qt_retorno_w
from	pls_segurado
where	nr_seq_titular		= nr_seq_titular_p
and	ie_situacao_atend	= 'A';

if (qt_retorno_w > 0) then
	ie_retorno_w	:= 'S';
else
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
