CREATE OR REPLACE FUNCTION pls_obter_descricao_mat_proc ( nr_seq_material_p bigint, cd_procedimento_p text, ie_origem_proc_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter descrição de procedimento e material dependendo do parâmetro passado.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);

BEGIN

if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	select	ds_material
	into STRICT	ds_retorno_w
	from	pls_material
	where	nr_sequencia = nr_seq_material_p;
elsif (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and (ie_origem_proc_p IS NOT NULL AND ie_origem_proc_p::text <> '') then
	select	ds_procedimento
	into STRICT	ds_retorno_w
	from	procedimento
	where 	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced 	= ie_origem_proc_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
