CREATE OR REPLACE FUNCTION pls_obter_descricao_proc_monit ( cd_procedimento_p procedimento.cd_procedimento%type, nr_seq_guia_monitor_ret_p pls_monitor_tiss_guia_ret.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Obter a descrição dos procedimentos no retorno do monitoramento,
	por padrão caso o procedimento possua tabela referência informada
	diferente de '00 - próprio da operadora' a origem do procedimento no retorno
	será TUSS, porém, devido a alguns clientes não terem o procedimento
	cadastrado com origem TUSS no Tasy, não havia descrição pro procedimento
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_guia_monitor_w		pls_monitor_tiss_guia_ret.nr_sequencia%type;
nr_seq_conta_w			pls_conta.nr_sequencia%type;
ie_orige_proced_w		pls_conta_proc.ie_origem_proced%type;
ds_procedimento_w		procedimento.ds_procedimento%type;


BEGIN
ds_procedimento_w := null;
if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	select	nr_seq_guia_monitor
	into STRICT	nr_seq_guia_monitor_w
	from	pls_monitor_tiss_guia_ret
	where	nr_sequencia = nr_seq_guia_monitor_ret_p;

	select	nr_seq_conta
	into STRICT	nr_seq_conta_w
	from	pls_monitor_tiss_guia
	where	nr_sequencia = nr_seq_guia_monitor_w;

	select	max(ie_origem_proced)
	into STRICT	ie_orige_proced_w
	from	pls_conta_proc
	where	nr_seq_conta = nr_seq_conta_w
	and	cd_procedimento = cd_procedimento_p;

	if (ie_orige_proced_w IS NOT NULL AND ie_orige_proced_w::text <> '') then
		select	ds_procedimento
		into STRICT	ds_procedimento_w
		from	procedimento
		where	cd_procedimento = cd_procedimento_p
		and	ie_origem_proced = ie_orige_proced_w;
	end if;
end if;

return	ds_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
