CREATE OR REPLACE FUNCTION pls_obter_descricao_prospect ( nr_seq_cliente_p pls_comercial_cliente.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select	nm_cliente_geracao
into STRICT	ds_retorno_w
from ( SELECT	substr(a.ds_razao_social,1,255) nm_cliente_geracao
	from	pls_comercial_cliente	b,
		pessoa_juridica		a
	where	a.cd_cgc	= b.cd_cgc
	and	b.nr_sequencia	= nr_seq_cliente_p
	and	(b.cd_cgc IS NOT NULL AND b.cd_cgc::text <> '')
	
union

	SELECT	substr(obter_nome_pf(a.cd_pessoa_fisica),1,255) nm_cliente_geracao
	from	pls_comercial_cliente	b,
		pessoa_fisica		a
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	b.nr_sequencia		= nr_seq_cliente_p
	and	coalesce(b.cd_cgc::text, '') = '' ) alias5;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
