CREATE OR REPLACE FUNCTION pls_obter_descricao_regiao ( nr_seq_regiao_p text) RETURNS varchar AS $body$
DECLARE


ds_regiao_w	varchar(100) := '';


BEGIN

if (coalesce(nr_seq_regiao_p,0) > 0) then

	select	max(ds_regiao)
	into STRICT	ds_regiao_w
	from	pls_regiao
	where	nr_sequencia = nr_seq_regiao_p;

end if;

return	ds_regiao_w;

end	;
$body$
