CREATE OR REPLACE FUNCTION pls_obter_desc_bonific_seg (nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_bonificacao_w	varchar(2000);
ds_bonificacao_ww	varchar(2000);

C01 CURSOR FOR
	SELECT	nm_bonificacao
	from	pls_bonificacao_vinculo	b,
		pls_bonificacao		a
	where	b.nr_seq_bonificacao	= a.nr_sequencia
	and	b.nr_seq_segurado	= nr_seq_segurado_p;


BEGIN
open C01;
loop
fetch C01 into
	ds_bonificacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_bonificacao_ww := ds_bonificacao_w||', '||ds_bonificacao_ww;
	ds_bonificacao_w	:= '';
	end;
end loop;
close C01;

return substr(ds_bonificacao_ww,1,length(ds_bonificacao_ww)-2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
