CREATE OR REPLACE FUNCTION pls_obter_desc_cooperativa ( cd_cooperativa_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);


BEGIN

begin
select	max(substr(b.ds_razao_social,1,255))
into STRICT	ds_retorno_w
from	pessoa_juridica b,
	pls_congenere a
where	a.cd_cgc		= b.cd_cgc
and	a.cd_cooperativa	= cd_cooperativa_p;
exception
when others then
	ds_retorno_w	:= '';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
