CREATE OR REPLACE FUNCTION pls_obter_desc_cpt_analise (nr_seq_pessoa_proposta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_carencia_w	varchar(2000);
ds_carencia_ww	varchar(2000);

C01 CURSOR FOR
	SELECT	ds_carencia
	from	pls_tipo_carencia	b,
		pls_carencia		a
	where	a.nr_seq_pessoa_proposta	= nr_seq_pessoa_proposta_p
	and	substr(pls_obter_se_cpt(nr_seq_tipo_carencia),1,1) ='S'
	and	a.nr_seq_tipo_carencia		= b.nr_sequencia;


BEGIN
open C01;
loop
fetch C01 into
	ds_carencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_carencia_ww := ds_carencia_w||', '||ds_carencia_ww;
	ds_carencia_w	:= '';
	end;
end loop;
close C01;

return substr(ds_carencia_ww,1,length(ds_carencia_ww)-2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
