CREATE OR REPLACE FUNCTION pls_obter_desc_end_ref ( cd_pessoa_fisica_p text, nr_seq_end_ref_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	max(nm_pessoa_fisica) || ' - ' ||
		max(CASE WHEN b.ie_tipo_complemento=1 THEN 'Residencial' WHEN b.ie_tipo_complemento=2 THEN 'Comercial' WHEN b.ie_tipo_complemento=3 THEN 'Responsável' WHEN b.ie_tipo_complemento=4 THEN 'Pai' WHEN b.ie_tipo_complemento=5 THEN 'Mãe' WHEN b.ie_tipo_complemento=6 THEN 'Conjuje' END )
	into STRICT	ds_retorno_w
	from	pessoa_fisica		a,
		compl_pessoa_fisica	b
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	b.nr_sequencia		= nr_seq_end_ref_p
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;

end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
