SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_espec_ptu ( cd_ptu_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
qt_registro_w		bigint := 0;


BEGIN
if (cd_ptu_p IS NOT NULL AND cd_ptu_p::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	especialidade_medica
	where	cd_ptu = cd_ptu_p;

	select	substr(max(ds_especialidade),1,255)
	into STRICT	ds_retorno_w
	from	especialidade_medica
	where	cd_ptu	= cd_ptu_p;
end if;

/* Se houver mais de uma especialidade com mesmo código PTU, então informar aquele que esta com o IE_PTU_A500 marcado,
     senão continua buscando pelo MAX da descrição da especialidade. */
if (qt_registro_w > 1) then
	select	coalesce(substr(max(ds_especialidade),1,255),ds_retorno_w)
	into STRICT	ds_retorno_w
	from	especialidade_medica
	where	cd_ptu		= cd_ptu_p
	and	ie_ptu_a500	= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_desc_espec_ptu ( cd_ptu_p bigint) FROM PUBLIC;

