CREATE OR REPLACE FUNCTION pls_obter_desc_etapa ( nr_seq_etapa_p pls_pendencias_prestador.nr_sequencial_pendencia%type, nr_sequencial_pendencia_p pls_pendencias_prestador.nr_sequencial_pendencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w varchar(255);


BEGIN
    select
        max(a.nr_sequencial_pendencia || ' - ' || b.ds_etapa) ds_seq_etapa
    into STRICT ds_retorno_w 
    from 
        pls_pendencias_prestador   a, 
        pls_acordo_sla             b 
    where 
        a.nr_seq_etapa = b.nr_sequencia 
        and a.nr_seq_etapa = nr_seq_etapa_p;

    if (coalesce(ds_retorno_w::text, '') = '' and nr_seq_etapa_p = 998) then
        ds_retorno_w := wsuite_util_pck.get_wsuite_expression(nr_seq_expression_param => 1058951, cd_estabelecimento_p => cd_estabelecimento_p);
    end if;

    if (coalesce(ds_retorno_w::text, '') = '' and nr_seq_etapa_p = 999) then
        ds_retorno_w := wsuite_util_pck.get_wsuite_expression(nr_seq_expression_param => 324860, cd_estabelecimento_p => cd_estabelecimento_p);
    end if;

    return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
