SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_fornec_ret_cot ( cd_cgc_prestador_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter a descrição do item de retorno da cotação
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255);
qt_prestador_w		bigint;



BEGIN

if (cd_cgc_prestador_p IS NOT NULL AND cd_cgc_prestador_p::text <> '') then

	-- Verificar primeiro se existe prestador com o CD_CGC informado
	select	count(1)
	into STRICT	qt_prestador_w
	from	pls_prestador
	where	cd_cgc		= cd_cgc_prestador_p;

	if (qt_prestador_w > 0) then

		select	ds_razao_social
		into STRICT	ds_retorno_w
		from	pessoa_juridica
		where	cd_cgc		= cd_cgc_prestador_p;
	end if;

	-- Caso não encontre, verificar se existe prestador com a sequencia passada como parâmetro
	if (coalesce(ds_retorno_w::text, '') = '') then

		select	count(1)
		into STRICT	qt_prestador_w
		from	pls_prestador
		where	nr_sequencia	= cd_cgc_prestador_p;

		if (qt_prestador_w > 0) then

			select	a.ds_razao_social
			into STRICT	ds_retorno_w
			from	pessoa_juridica	a,
				pls_prestador	b
			where	b.cd_cgc		= a.cd_cgc
			and	b.nr_sequencia		= cd_cgc_prestador_p;
		end if;
	end if;

	-- Caso não encontre, verificar se existe prestador fornecedor do material (OPS - Cadastro de materiais / Fornecedores)
	if (coalesce(ds_retorno_w::text, '') = '') then

		select	nm_fornecedor
		into STRICT	ds_retorno_w
		from	pls_fornecedor_material
		where	cd_cgc 		= cd_cgc_prestador_p;
	end if;

	-- Caso não encontre, verificar se existe prestador fornecedor do material com a sequencia passada como parâmetro
	if (coalesce(ds_retorno_w::text, '') = '') then

		select	nm_fornecedor
		into STRICT	ds_retorno_w
		from	pls_fornecedor_material
		where	nr_sequencia 		= cd_cgc_prestador_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_desc_fornec_ret_cot ( cd_cgc_prestador_p text) FROM PUBLIC;

