CREATE OR REPLACE FUNCTION pls_obter_desc_ger_tab_propria (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


dt_inicial_w		timestamp;
dt_final_w		timestamp;
ds_regra_w		varchar(255);
ie_tipo_despesa_w	varchar(2);
ds_retorno_w		varchar(255);


BEGIN
select	max(dt_inicio_vigencia),
	max(dt_fim_vigencia),
	max(ds_regra),
	max(ie_tipo_despesa)
into STRICT	dt_inicial_w,
	dt_final_w,
	ds_regra_w,
	ie_tipo_despesa_w
from	pls_lote_regra_tab_propria
where	nr_sequencia = nr_sequencia_p;

ds_retorno_w := substr(ds_regra_w,1,55)||' - '||to_char(dt_inicial_w,'dd/mm/yy');

if (dt_final_w IS NOT NULL AND dt_final_w::text <> '') then
	begin
	ds_retorno_w := ds_retorno_w || ' - ' ||to_char(dt_final_w,'dd/mm/yy');
	exception
	when others then
		null;
	end;
end if;

if (ie_tipo_despesa_w IS NOT NULL AND ie_tipo_despesa_w::text <> '') then
	begin
	ds_retorno_w := ds_retorno_w || ' - ' ||substr(Obter_Valor_Dominio(1854,ie_tipo_despesa_w),1,20);
	exception
	when others then
		null;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_desc_ger_tab_propria (nr_sequencia_p bigint) FROM PUBLIC;

