CREATE OR REPLACE FUNCTION pls_obter_desc_glosas_obs ( nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000);
ds_menssagem_w	varchar(255);
nr_seq_motivo_glosa_w		bigint;

C01 CURSOR FOR
	SELECT	distinct(nr_seq_motivo_glosa)
	from	pls_guia_glosa
	where (nr_seq_guia		= nr_seq_guia_p
	or	nr_seq_guia_mat		in (	SELECT	x.nr_sequencia
						from 	pls_guia_plano_mat x
						where 	x.nr_seq_guia = nr_seq_guia_p)
	or	nr_seq_guia_proc	in (	select	y.nr_sequencia
						from	pls_guia_plano_proc y
						where	y.nr_seq_guia = nr_seq_guia_p))
	and	nr_seq_motivo_glosa	in (	select	z.nr_seq_motivo_glosa	from pls_msg_externa_glosa z);


BEGIN

ds_retorno_w	:= 'X';

open C01;
loop
fetch C01 into
	nr_seq_motivo_glosa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ds_retorno_w	= 'X') then
		ds_retorno_w	:= pls_obter_dados_msg_glosa(nr_seq_motivo_glosa_w,'ME');
	elsif (ds_retorno_w	<> 'X') then
		ds_retorno_w	:= ds_retorno_w||', '||pls_obter_dados_msg_glosa(nr_seq_motivo_glosa_w,'ME');
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
