++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255);


BEGIN
select	ds_grupo
into STRICT	ds_retorno_w
from	pls_preco_grupo_doenca
where	nr_sequencia = nr_seq_grupo_p;

return	ds_retorno_w;

end;
$body$
