CREATE OR REPLACE FUNCTION pls_obter_desc_grupo_pacote ( nr_seq_grupo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_grupo_w			varchar(255);


BEGIN

select	substr(max(ds_grupo),1,255)
