CREATE OR REPLACE FUNCTION pls_obter_desc_inscricao ( nr_seq_inscricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);


BEGIN

select	substr('Da '||qt_parcela_inicial||'° à '||qt_parcela_final||'° parcela - Vl. inscrição:'||Campo_Mascara_virgula(vl_inscricao)||' - Tx. inscrição:'||tx_inscricao||'% '||CASE WHEN ie_grau_dependencia='T' THEN ' - Titulares' WHEN ie_grau_dependencia='D' THEN ' - Dependentes' END ,1,255)
into STRICT	ds_retorno_w
from	pls_regra_inscricao
where	nr_sequencia	= nr_seq_inscricao_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
