/*
ie_singular_p
P	Plural
S	Singular
*/
BEGIN

select	CASE WHEN ie_singular_p='S' THEN  CASE WHEN ds_tipo_despesa_p='P1' THEN 'PROCEDIMENTO' WHEN ds_tipo_despesa_p='P2' THEN 'TAXA' WHEN ds_tipo_despesa_p='P3' THEN 'DIÁRIA' WHEN ds_tipo_despesa_p='P4' THEN 'PACOTE' WHEN ds_tipo_despesa_p='M1' THEN 'GASE' WHEN ds_tipo_despesa_p='M2' THEN 'MEDICAMENTO' WHEN ds_tipo_despesa_p='M3' THEN 'MATERIAL' WHEN ds_tipo_despesa_p='M7' THEN 'OPM' END   ELSE CASE WHEN ds_tipo_despesa_p='P1' THEN 'PROCEDIMENTOS' WHEN ds_tipo_despesa_p='P2' THEN 'TAXAS' WHEN ds_tipo_despesa_p='P3' THEN 'DIÁRIAS' WHEN ds_tipo_despesa_p='P4' THEN 'PACOTES' WHEN ds_tipo_despesa_p='M1' THEN 'GASES' WHEN ds_tipo_despesa_p='M2' THEN 'MEDICAMENTOS' WHEN ds_tipo_despesa_p='M3' THEN 'MATERIAIS' WHEN ds_tipo_despesa_p='M7' THEN 'OPM' END  END
into STRICT	ds_retorno_w
;

return	ds_retorno_w;

end;
