CREATE OR REPLACE FUNCTION pls_obter_desc_item_qual_monit (cd_item_p pls_material.cd_material_ops%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		pls_material.ds_material%type;


BEGIN

select	max(ds_procedimento)
into STRICT	ds_retorno_w
from	procedimento
where	cd_procedimento = cd_item_p
and	ie_situacao = 'A';

if (coalesce(ds_retorno_w::text, '') = '') then

	select	max(ds_procedimento)
	into STRICT	ds_retorno_w
	from	procedimento
	where	cd_procedimento = cd_item_p;

	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(ds_material)
		into STRICT	ds_retorno_w
		from 	pls_material
		where	cd_material_ops = cd_item_p
		and	ie_situacao = 'A';

		if (coalesce(ds_retorno_w::text, '') = '') then

			select	max(ds_material)
			into STRICT	ds_retorno_w
			from 	pls_material
			where	cd_material_ops = cd_item_p;

			if (coalesce(ds_retorno_w::text, '') = '') then

				select	max(ds_material)
				into STRICT	ds_retorno_w
				from 	pls_material
				where	cd_material = cd_item_p
				and	ie_situacao = 'A';

				if (coalesce(ds_retorno_w::text, '') = '') then

					select	max(ds_material)
					into STRICT	ds_retorno_w
					from 	pls_material
					where	cd_material = cd_item_p;
				end if;
			end if;
		end if;
	end if;
end if;


return	ds_retorno_w;

end;
