CREATE OR REPLACE FUNCTION pls_obter_desc_item_resumo ( nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_item_w		varchar(255);
cd_procedimento_w	bigint;
cd_item_w		bigint;
ie_origem_proced_w	bigint;
cd_material_w		bigint;


BEGIN
select	max(a.cd_material_alt),
	max(a.cd_procedimento_alt),
	max(a.ie_origem_proced_alt),
	max(a.cd_item),
	max(a.ds_item)
into STRICT	cd_material_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	cd_item_w,
	ds_item_w
from	w_pls_resumo_conta	a
where	a.nr_sequencia	= nr_seq_item_p;

if (coalesce(cd_material_w::text, '') = '') and (coalesce(cd_procedimento_w::text, '') = '') then
	ds_retorno_w	:= ds_item_w;
else
	if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then
		select	max(a.ds_material)
		into STRICT	ds_retorno_w
		from	pls_material	a
		where	a.cd_material_ops	= to_char(cd_material_w);
	else
		ds_retorno_w	:= obter_descricao_procedimento(cd_procedimento_w,ie_origem_proced_w);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
