CREATE OR REPLACE FUNCTION pls_obter_desc_item_ret_cot ( nr_seq_item_cotacao_p bigint, cd_item_retorno_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter a descrição do item de retorno da cotação
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_item_w		varchar(255);
ds_procedimento_w	varchar(255);
ds_material_w		varchar(255);
cd_procedimento_w	pls_item_cotacao.cd_procedimento%type;
nr_seq_material_w	pls_item_cotacao.nr_seq_material%type;


BEGIN

if (cd_item_retorno_p IS NOT NULL AND cd_item_retorno_p::text <> '') then

	select	ds_material
	into STRICT	ds_item_w
	from	pls_material
	where	nr_sequencia	= cd_item_retorno_p;
end if;

return	ds_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
