SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_material_regra ( nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255)	:= '';
qt_regra_w		bigint;


BEGIN
select	count(1)
into STRICT	qt_regra_w
from 	pls_material_restricao x
where 	x.nr_seq_material	= nr_seq_material_p
and 	coalesce(x.ie_bloqueia_autor,'N') = 'S'
and	clock_timestamp()	between	dt_inicio_vigencia and coalesce(dt_fim_vigencia, clock_timestamp());

if (qt_regra_w = 0) then
	begin
		select	ds_material
		into STRICT	ds_retorno_w
		from	pls_material
		where	nr_sequencia	= nr_seq_material_p;
	exception
	when no_data_found then
		ds_retorno_w	:= '';
	end;
else
	ds_retorno_w	:= '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
